/* import {
  ADD_TO_CART,
  CHECKOUT_REQUEST,
  CHECKOUT_FAILURE
} from '../constants/ActionTypes' */

//export default 只导出一个
// const tiger=0;
const initialState = {
  addedIds: [],
  quantityById: {}
}

export default reducer= (state=initialState,action)=>{ 
        switch(action.type){
            case "INCREMENT":
            return state +1
            case "DECREMENT":
            return  state -1
            default:
            return state
        }
    }

