import React, { Component } from 'react'
import {
  Form, Select, InputNumber, Button, Input
} from 'antd';
import { Link, withRouter } from 'react-router-dom';
import './Edit.less';

const { Option } = Select;
const FormItem = Form.Item;
const ButtonGroup = Button.Group;

class Temp extends Component {
  handleReset(e) {
    //resetFields重置表单内容
    e.preventDefault();
    this.props.props.form.resetFields();
  };
  userExists(rule, value, callback) {
    //当做validator函数来用,用来作为判断条件
    if (!value) {
      callback();
    } else {
      setTimeout(() => {
        if (value === '宫爆鸡丁') {
          callback([new Error('抱歉，该菜名已被占用。')]);
        } else {
          callback();
        }
      }, 800);
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  }
  normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  }


  render() {
    console.log(this.props.location.state)//数据
    //分页组件提取出来，先进行一步数据是否获得到的判断，因为render渲染多次，前几次没有数据
    //  
    // return (<div>21321321</div>)
    const title = ["姓名","性别","身份证号","出生年月","年龄","入职时间","职位","状态"];
    const data = { name: "" };
    const { getFieldProps, getFieldError, isFieldValidating } = this.props.form;

    const nameProps = getFieldProps('name', { //第一个参数为ID,第二个为options
      initialValue: 1,
      rules: [
        { required: true, min: 1, message: '菜品名至少为 1 个字符' },
        { validator: this.userExists.bind(this) }
      ]
    });

    const selectProps = getFieldProps('select', {
      initialValue: 2,
      rules: [
        { required: true, message: '请选择菜品类型' },
      ],
    });

    const priceProps = getFieldProps('price', {//textarea 为ID
      initialValue: 4,
    });

    const formItemLayout = {//样式
      labelCol: { span: 7 },
      wrapperCol: { span: 12 },
    };
    return (
      <section className="clerk-info">
        <div className="wrap">
          <Form >
            <FormItem
              {...formItemLayout}
              label="菜品名称："
              hasFeedback
              help={isFieldValidating('name') ? '校验中...' : (getFieldError('name') || []).join(', ')}
            >
              <Input {...nameProps} placeholder=" 输入 宫爆鸡丁 看看"
              />
            </FormItem>

            <FormItem
              label="菜品类型："
              {...formItemLayout}
              required
            >
              <Select style={{ width: 200 }}
                {...selectProps}
              >
                <Option value="特色汤面">特色汤面</Option>
                <Option value="酒水饮料">酒水饮料</Option>
                <Option value="烧烤系列">烧烤系列</Option>
                <Option value="美味盖饭">美味盖饭</Option>
                <Option value="小吃，肉夹馍">小吃，肉夹馍</Option>
                <Option value="海鲜" disabled>海鲜</Option>
              </Select>
            </FormItem>

            <ButtonGroup style={{ borderRadius: 15, marginTop: 30 }}>
              <Button
                onClick={this.handleReset.bind(this)}
                style={{ marginLeft: 400 }}>Reset</Button>
            </ButtonGroup>
          </Form >
        </div>
      </section>
    )
  }
}
const WrappedDemo = Form.create({ name: 'validate_other' })(Temp);

export default withRouter(WrappedDemo)
// export default Temp
