import React, {
    Component,
    Fragment
} from 'react'
import {
    Button,
    Pagination,
    Switch,
    Checkbox,
    Select,
    Table,
    Modal
} from 'antd';
import IconFont from '../../../../iconfont/font'
import './List.less';
/*   -----------------     redux引入部分      ------------------- */
import {
    withRouter
} from 'react-router-dom';
import * as actionCreators from '../../../../redux/actions/actions';
import {
    connect
} from 'react-redux'
import {
    bindActionCreators
} from "redux"
import axios from 'axios';


const Option = Select.Option;
const confirm = Modal.confirm;



class Temp extends Component {
    state = {
        selectedRowKeys: [], // Check here to configure the default column
        loading: false,
    };
    start = () => {
        this.setState({
            loading: true
        });
        // ajax request after empty completing
        setTimeout(() => {
            this.setState({
                selectedRowKeys: [],
                loading: false,
            });
        }, 1000);
    }

    onSelectChange = (selectedRowKeys) => {
        // member_num
        console.log('selectedRowKeys changed: ', selectedRowKeys);
        this.setState({
            selectedRowKeys
        });
    }
    async componentDidMount() {
        await this.props.fetchOrderInfo(null, 1)
    }
    pageChange = async (page) => {
        await this.props.fetchOrderInfo(null, page.current)
    }
    /* ------------------    订单删除部分(以下)   ------------------- */

    showDeleteConfirm = (record) => {
        confirm({
            title: 'Are you sure delete this task?',
            okText: 'Yes',
            okType: 'danger',
            cancelText: 'No',
            onOk() {
                fetch(`http://localhost:2000/orders?act=delOrder`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(record)
                })
                axios.get(`http://localhost:2000/orders/list?num=${page}`)
                    .then(function (data) {
                        console.log(data.data)

                        let {
                            filterArr,
                            total
                        } = {
                            ...(data.data)
                        }
                        console.log(filterArr, total);
                        return dispatch(getOrderInfo(filterArr, total))
                    })
                    .catch(function (error) {
                        console.log(error);
                    });



            },
            onCancel() {
                console.log('Cancel');
            },
        });
    }
    /* ------------------    订单删除部分(以上)   ------------------- */

    /* ------------------    列表渲染部分(以下)   ------------------- */

    columns = [{
        title: '序号',
        dataIndex: 'num',
    }, {
        title: '订单编号',
        dataIndex: 'order_id',
    }, {
        title: '下单时间',
        dataIndex: 'created_at',
    }, {
        title: '用户账号',
        dataIndex: 'user',
    }, {
        title: '订单金额',
        dataIndex: 'total_price',
    }, {
        title: '订单状态',
        dataIndex: 'state',
    }, {
        title: '操作',
        dataIndex: 'constrol',
        render: (text, record) => ( <
            Fragment key = {
                record
            } >
            <
            Button type = "primary"
            size = "small"
            ghost style = {
                {
                    marginRight: 20,
                    fontSize: 12,
                    height: 28,
                    padding: "5px 13px",
                    background: " #fff",
                    border: " 1px solid #dcdfe6",
                    color: " #606266",
                    borderRadius: 2
                }
            } > 查看详情 < /Button> <
            Button size = "small"
            onClick = {
                () => this.showDeleteConfirm(record)
            }
            style = {
                {
                    marginRight: 10,
                    fontSize: 12,
                    height: 27,
                    padding: "5px 20px",
                    backgroundColor: "#f56c6c",
                    border: "1px solid #f56c6c",
                    color: "#fff",
                    borderRadius: 2
                }
            } > 删除 < /Button> <
            /Fragment>
        ),
    }];
    /* ------------------    列表渲染部分(以上)   ------------------- */

    render() {
        const {
            loading,
            selectedRowKeys
        } = this.state;
        const rowSelection = {
            selectedRowKeys,
            onChange: this.onSelectChange,
        };
        const hasSelected = selectedRowKeys.length > 0;
        const status_code = {
            "-5": "等待支付",
            "-4": "支付失败",
            "-1": "订单取消",
            "0": "订单未处理",
            "2": "订单已处理",
            "11": "用户确认订单",
        }

        let data = [];
        if (this.props.data.length) {
            console.log(this.props.data)
            for (let ele of this.props.data) {
                // console.log(ele.data[0])
                data.push({
                    key: ele.data[0].member_num,
                    num: ele.data[0].user_id,
                    order_id: ele.data[0].order_id,
                    created_at: ele.data[0].created_at,
                    user: ele.data[0].user,
                    total_price: ele.data[0].total_price.toFixed(2),
                    state: status_code[ele.data[0].status_code * 1]
                });
            }
        }

        console.log(this.state.selectedRowKeys)
        return ( <
            section className = "order-info" > {
                /*   员工信息组件  */ } <
