import React, { Component } from 'react'
import { Button, Pagination, Switch, Checkbox, Select, Popconfirm, message } from 'antd';
import IconFont from '../../../../iconfont/font'
import './List.less';
import { withRouter } from 'react-router-dom';

//  -----------------     redux      ----------
import * as actionCreators from '../../../../redux/actions/actions';
import { connect } from 'react-redux'
import { bindActionCreators } from "redux"
import { reject, all } from 'q';

const Option = Select.Option;
const text = 'Are you sure to delete this task?';

class Temp extends Component {
    constructor() {
        super()
        this.Page = null;  //用于包装Pagination组件;
        this.itemsArr = []; //初始化数组,page切换,清空
        this.renderArr = ["热销", "推荐", "折扣"];
        this.renderData = null;        // 渲染的数据
        this.selectOption = ["设为推荐", "设为热销", "设为折扣", "批量删除"]
        this.selectValue = ""
        this.state = {
            current: 1,
            storeData: [],        // 菜品数据
            categoryName: {},      // 菜品类名 
            indeterminate: false, // 全选按钮 中间状态控制
            checkAll: false,      // 全部选中
            storeArr: []         // 状态切换存储数据
        }
    }
    async componentDidMount() { // 重复工作尽量用生命周期
        await this.props.fetchFoodInfo()  //!!!!!!! 终点请求数据异步,拿不到
        // console.log(this.props.data)
        this.initData(this.props.data)
    }
    initData(inputData) { //数据初始化,数据格式处理
        let temp = []
        let categoryName = {}//类名
        // console.log(this.props.data)
        for (let ele of inputData.data) {
            categoryName[ele.categoryName] = ele.categoryName//存储类名
            for (let item of ele.spuList) {//对拿回来数据处理
                item.categoryName = ele.categoryName //类名
                temp.push(item)
            }
        }
        this.setState({
            storeData: temp,
            categoryName
        })
    }
    /* ------------------    信息内容列表部分渲染(以下)   ------------------- */
    // EditClick(ele, ev) {//点击编辑
    //     ev.preventDefault();
    //     // console.log(ele);
    //     this.props.history.push({//将此条完整菜品信息藏在state中
    //         pathname: "/food/listDetails",
    //         state: { ele, categoryName: this.state.categoryName },
    //         search: '?num=' + ele.spuId
    //     });
    // }
    renderItems = (ele, index) => {
        if (this.itemsArr.length < 5) {
            this.itemsArr.push(false)
           
        }
        return (
            <tr key={ele.spuId} id={ele.spuId}>
                <td>
                    < Checkbox
                        onChange={(ev) => { this.checkedChange(index, ev) }}//传参小难点
                        // id={`num${index}`}
                        // indeterminate={this.state.storeArr[index]}
                        checked={this.state.storeArr[index]}
                    />
                </td>
                <td>{ele.spuId}</td>
                <td><img src={ele.littleImageUrl} alt={ele.spuName} /></td>
                <td>{ele.spuName}</td>
                <td > {ele.categoryName} </td>
                <td>{ele.saleVolume}</td>
                <td>￥{ele.currentPrice}</td>
                <td>
                    {this.renderArr.map((item) =>
                        <p key={item}>
                            <span style={{ paddingRight: 15 }}>{item}</span>
                            <Switch
                                size="default"
                                defaultChecked={ele.categoryName === item}
                                onChange={(valid) =>this.props.transRecommend(valid, item, ele)}
                            />
                        </p>
                    )}

                </td>
                <td>
                    <Button
                        type="primary"
                        size="small"
                        ghost="true"
                        onClick={(ev) => { this.EditClick(ele, ev) }}//传参为tr索引
                        style={{ marginRight: 10, fontSize: 13, width: 60, height: 25, borderRadius: 5 }}
                    >
                        <span>编辑</span>
                    </Button>

                    <Popconfirm
                        placement="topRight"
                        title={text}
                        onConfirm={this.confirm}
                        okText="Yes"
                        onConfirm={() => { this.deleteInfo(ele) }}
                        cancelText="No">
                        <Button
                            type="primary"
                            size="small"
                            // onClick={() => { this.deleteInfo(ele) }}
                            style={{ fontSize: 13, width: 60, height: 25, borderRadius: 5 }}
                        >删除</Button>
                    </Popconfirm>
                </td>
            </tr>)
    }
    checkedChange = (index, ev) => { // 单个点击事件 index 为传入索引
        ev.stopPropagation()
        this.itemsArr[index] = ev.target.checked
        if (this.itemsArr.every(ele => ele === true)) {
            // 多选框全为 => true,全选按钮为true,indeterminate为false(indeterminate中间状态)
            this.setState({
                storeArr: this.itemsArr,  // 为了render
                checkAll: true,
                indeterminate: false
            })
        } else {
            this.setState({ // 存在多选框为 false
                storeArr: this.itemsArr,
                checkAll: false,
                indeterminate: true
            })
        }
        if (!this.itemsArr.some(ele => ele === true)) {
            //多选框全为 => false,indeterminate为false
            this.setState({
                storeArr: this.itemsArr,
                indeterminate: false
            })
        }
    }
    onCheckAllChange = (e) => {
        if (e.target.checked) {
            this.itemsArr.forEach((ele, index) => {
                this.itemsArr[index] = e.target.checked
            });
        }
        else {
            this.itemsArr.forEach((ele, index) => {
                this.itemsArr[index] = e.target.checked
            });
        }
        this.setState({
            checkAll: e.target.checked,
            storeArr: this.itemsArr,
            indeterminate: false
        })

    }
    confirm = () => {//气泡确认框内容
        message.info('Clicked on Yes.');
    }

    /* ------------------    信息内容列表部分渲染(以上)   ------------------- */


    //-------------------------- 批量操作部分(以下) --------------------
    bulkOperation = () => {
        /* 思路： 
            1.首先判断选中内容是哪些？(先看一下数组准不准,state 还是外面的准一些);
            2.确定选中内容后,判断select中内容;(附加条件select数值有效)
            3.点击确定发送请求,将选中数据发送到后台进行修改;
            4.后台接收到的是一个数组,怎样处理进行数组解构;
            5.后台进行判断,如果是一个数组一条一条增加处理？？
        */
        let tempArr = [] // 对应
