import React, {
    Component,
    Fragment
} from 'react'
import {
    Button,
    Pagination,
    Switch,
    Checkbox,
    Select,
    Table
} from 'antd';
import IconFont from '../../../../iconfont/font'
import './List.less';
/*   -----------------     redux引入部分      ------------------- */
import {
    withRouter
} from 'react-router-dom';
import * as actionCreators from '../../../../redux/actions/actions';
import {
    connect
} from 'react-redux'
import {
    bindActionCreators
} from "redux"


const Option = Select.Option;
const columns = [{
    title: '序号',
    dataIndex: 'num',
}, {
    title: '订单编号',
    dataIndex: 'order_id',
}, {
    title: '下单时间',
    dataIndex: 'created_at',
}, {
    title: '用户账号',
    dataIndex: 'user',
}, {
    title: '订单金额',
    dataIndex: 'total_price',
}, {
    title: '订单状态',
    dataIndex: 'state',
}, {
    title: '操作',
    dataIndex: 'constrol',
    render: (text, record) => ( <
        Fragment key = {
            record
        } >
        <
        Button type = "primary"
        size = "small"
        ghost style = {
            {
                marginRight: 10,
                fontSize: 12,
                height: 26,
                padding: "0px 13px",
                background: " #fff",
                border: " 1px solid #dcdfe6",
                color: " #606266",
                borderRadius: 2
            }
        } > 查看详情 < /Button> <
        Button size = "small"
        style = {
            {
                marginRight: 10,
                fontSize: 12,
                height: 26,
                padding: "0px 15px",
                backgroundColor: "#f5222d",
                border: "1px solid #f5222d",
                color: "#fff",
                borderRadius: 2
            }
        } > 删除 < /Button> <
        /Fragment>
    ),
}];

const data = [];
for (let i = 0; i < 20; i++) {
    data.push({
        key: i,
        num: i,
        order_id: i,
        name: `sss King ${i}`,
        created_at: `aaa King ${i}`,
        user: `sss King ${i}`,
        total_price: `ffff King ${i}`,
        state: `eeeee King ${i}`,
    });
}



class Temp extends Component {
    state = {
        selectedRowKeys: [], // Check here to configure the default column
        loading: false,
    };
    start = () => {
        this.setState({
            loading: true
        });
        // ajax request after empty completing
        setTimeout(() => {
            this.setState({
                selectedRowKeys: [],
                loading: false,
            });
        }, 1000);
    }

    onSelectChange = (selectedRowKeys) => {
        console.log('selectedRowKeys changed: ', selectedRowKeys);
        this.setState({
            selectedRowKeys
        });
    }
    componentDidMount() {
        console.log(this)
    }
    render() {
        const {
            loading,
            selectedRowKeys
        } = this.state;
        const rowSelection = {
            selectedRowKeys,
            onChange: this.onSelectChange,
        };
        const hasSelected = selectedRowKeys.length > 0;

        return ( <
            section className = "order-info" > {
                /*   员工信息组件  */ } <
            div className = "app-container" >
            <
            div className = "el-serch-wrap" >
            <
            div className = "el-title-body" >
            <
            div >
            <
            IconFont type = "mall-doc-glass"
            style = {
                {
                    fontSize: 16,
                    marginRight: 5
                }
            }
            /> <
            span > 筛选检索 < /span> <
            button className = "add" > 查询结果 < /button> <
            Button className = "btn"
            style = {
                {
                    color: "#1890ff",
                    float: "right",
                    marginRight: 20,
                }
            }
            ghost
            // onClick={this.handleResetClick.bind(this)}
            >
            重置 < /Button> <
            /div> <
            div className = "el-form-item" >
            <
            span > 输入检索: < /span> <
            input type = "text"
            placeholder = "请输入订单编号" >
            <
            /input> <
            /div> <
            div className = "el-form-item" >
            <
            span > 收货人: < /span> <
            input type = "text"
            placeholder = "收货人手机号/联系人" >
            <
            /input> <
            /div> <
            div className = "el-form-item" >
            <
            span > 订单状态: < /span> <
            Select showSearch placeholder = "菜品类型"
            style = {
                {
                    width: 170,
                    color: "#606266",
                    fontSize: 12
                }
            } >
            <
            Option value = "待付款" > 待付款 < /Option> <
            Option value = "待发货" > 待发货 < /Option> <
            Option value = "已发货" > 已发货 < /Option> <
            Option value = "已完成" > 已完成 < /Option> <
            Option value = "已关闭" > 已关闭 < /Option> <
            /Select>, <
            /div> <
            /div> <
            /div> <
            div className = "el-title" >
            <
            div className = "el-title-body" >
            <
            IconFont type = "mall-doc-list"
            style = {
                {
                    fontSize: 16,
                    marginRight: 5
                }
            }
            /> <
            span > 数据列表 < /span> <
            /div> <
            /div> <
            Fragment >
            <
            Table rowSelection = {
                rowSelection
            }
            columns = {
                columns
            }
            dataSource = {
                data
            }
            bordered className = "el-table" / >
            <
            Select
            // defaultValue= {this.state.defalutSelecctValue} //不能改变颜色
            placeholder = "批量操作" //默认值
            style = {
                {
                    position: "absolute",
                    width: 150,
                    size: "large",
                    color: this.state.colorBoolean ? "rgba(0, 0, 0, 0.45)" : "rgba(0, 0, 0, 1)"
                    // 颜色控制方法: 初始一个颜色下拉选择后一个颜色,page改变后内容和文字颜色都恢复
                }
            }
            value = {
                this.state.selecctValue
            } >
            {
                /* {this.selectOption.map(item =>
                                            <Option value={item} key={item} >{item}</Option>
                                        )} */
            } <
            /Select> <
            Button type = "primary"
            style = {
                {
                    position: "absolute",
                    fontSize: 13,
                    left: 245,
                    height: 30,
                    size: "large"
                }
            }
            onClick = {
                this.start
            }
            disabled = {
                !hasSelected
            }
            loading = {
                loading
            } >
            确定 < /Button> <
            span style = {
                {
                    marginLeft: 8,
                    marginLeft: 16
                }
            } > {
                hasSelected ? `Selected ${selectedRowKeys.length} items` : ''
            } <
            /span>

            <
            /Fragment>

            <
            Table columns = {
                self.tableColumns
            } //th菜单项
            rowKey = {
                record => record.registered
            }
            dataSource = {
                this.state.dataSource.data
            } //数据
            pagination = {
                { //分页
                    total: this.state.dataSource.count, //数据总数量
                    pageSize: this.state.queryInfo.pageSize, //显示几条一页
                    defaultPageSize: this.state.queryInfo.pageSize, //默认显示几条一页
                    showSizeChanger: true, //是否显示可以设置几条一页的选项
                    onShowSizeChange(current, pageSize) { //当几条一页的值改变后调用函数，current：改变显示条数时当前数据所在页；pageSize:改变后的一页显示条数
                        self.toSelectchange(current, pageSize); //这边已经设置了self = this
                    },
                    onChange(current) { //点击改变页数的选项时调用函数，current:将要跳转的页数
                        self.gotoThispage(current, self.state.queryInfo.pageSize);
                    },
                    showTotal: function () { //设置显示一共几条数据
                        return '共 ' + this.state.dataSource.count + ' 条数据';
                    }
                }
            }
            loading = {
                this.state.loading
            } //设置loading属性
            /> {
                /* <main className="table-container">
                                        <table cellPadding="0" cellSpacing="0">
                                            <thead>
                                                <tr>
                                                    <th> < Checkbox /></th>
                                                    <th>编号</th>
                                                    <th>订单编号</th>
                                                    <th>提交时间</th>
                                                    <th>用户账号</th>
                                                    <th>订单金额</th>
                                                    <th>订单状态</th>
                                                    <th>操作</th>
                                                </tr>
                                            </thead>
