import React, { Component } from 'react'
import { Form, Input, Select, Upload, Icon, Modal } from 'antd';

import './Add.less';

const FormItem = Form.Item;
const Option = Select.Option;

export default class Temp extends Component {
  state = {
    previewVisible: false,//控制Modal是否显示（modal控制图片显示）
    previewImage: '',
    fileList: [{
      uid: '-1',
      name: 'xxx.png',
      status: 'done',
      url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
    }],
  };
  handleCancel = () => this.setState({ previewVisible: false })

  handlePreview = (file) => {
    this.setState({
      previewImage: file.url || file.thumbUrl,
      previewVisible: true,
    });
  }

  handleChange = ({ fileList }) => this.setState({ fileList })

  render() {
    const { getFieldProps, getFieldError, isFieldValidating, getFieldDecorator } = this.props.props.form;
    const formItemLayout = {//样式
      labelCol: { span: 7 },
      wrapperCol: { span: 12 },
    };
    const selectProps = getFieldProps('promot');

    const { previewVisible, previewImage, fileList } = this.state;

    const uploadButton = (//添加 控制 按钮
      <div>
        <Icon type="plus" />
        <div className="ant-upload-text">Upload</div>
      </div>
    );
    return (
      <Form >
        <FormItem
          {...formItemLayout}
          label="菜品名称："
        >
          {/* 给input设置名字和规则方法    之  其二 */}
          {getFieldDecorator('loading')(
            <Input />
          )}
        </FormItem>

        <FormItem
          label="菜品类型："
          {...formItemLayout}
        >
          {/* require 代表前面的 * 符号 */}
          <Select style={{ width: 200 }}
            {...selectProps}
          >
            <Option value="新品">新品</Option>
            <Option value="热销">热销</Option>
            <Option value="买过">买过</Option>
            <Option value="优惠">优惠</Option>
            <Option value="套餐">套餐</Option>
            <Option value="为您优选">为您优选</Option>
          </Select>
        </FormItem>

        <FormItem
          label="菜品照片："
          {...formItemLayout}
        >
          {/* <div>
            <Upload
              action="//jsonplaceholder.typicode.com/posts/"
              listType="picture-card"
              // onPreview={this.handlePreview}//点击文件链接或预览图标时的回调	
              onChange={this.handleImageChange}//上传文件改变时的状态
            >
              {fileList.length >= 2 ? null : uploadButton}
            </Upload>
             <Modal 
             visible={previewVisible} 
             footer={null} 
             onCancel={this.handleImageCancel}>
              <img alt="example" style={{ width: '50%' }}
               src={previewImage} />
            </Modal>
          </div> */}
  <div className="clearfix">
        <Upload
          action="//jsonplaceholder.typicode.com/posts/"
          listType="picture-card"
          fileList={fileList}
         
          onChange={this.handleChange}
        >
          {fileList.length >= 2 ? null : uploadButton}
        </Upload>
        <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
          <img alt="example" style={{ width: '100%' }} src={previewImage} />
        </Modal>
      </div>


        </FormItem>
      </Form >
    )
  }
}
