import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Icon, Breadcrumb } from 'antd';
import App from './App';

if(module.hot)
{
    module.hot.accept();
}

const { Header, Sider, Content } = Layout;
const SubMenu = Menu.SubMenu;

class SiderDemo extends React.Component {
  state = {
    collapsed: false,
  };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  }

  render() {
    return (
      <Layout>
        <Sider
          trigger={null}
          collapsible
          collapsed={this.state.collapsed}
          onCollapse={this.onCollapse}
        >
          <div className="logo" />
          {/* 首页 */}
          <Menu theme="dark" mode="inline" 
         >
          <Menu.Item key="1" >
          <Icon type="user" />
          <span>首页</span>
        </Menu.Item>
          </Menu>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={['2']}>
          <SubMenu theme="dark" mode="inline" defaultSelectedKeys={['3']}  title={<span><Icon type="team" /><span>人员管理</span></span>}>
            <Menu.Item key="1" >
              <Icon type="user" />
              <span>店员列表</span>
            </Menu.Item>
            <Menu.Item key="2">
              <Icon type="video-camera" />
              <span>会员列表</span>
            </Menu.Item>
          </SubMenu>
          </Menu>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={['2']}>
          <SubMenu theme="dark" mode="inline" defaultSelectedKeys={['3']}  title={<span><Icon type="team" /><span>菜品管理</span></span>}>
            <Menu.Item key="1" >
              <Icon type="user" />
              <span>菜品列表</span>
            </Menu.Item>
            <Menu.Item key="2">
              <Icon type="video-camera" />
              <span>添加</span>
            </Menu.Item>
          </SubMenu>
          </Menu>
            {/* 项目地址及链接 */}
        <Menu theme="dark" mode="inline" defaultSelectedKeys={['4']}
         >
          <Menu.Item key="1" >
          <Icon type="user" />
          <span>项目地址</span>
        </Menu.Item>
          </Menu>

        </Sider>
        <Layout>
          <Header style={{ background: '#fff', padding: 0,height: 50,border:'1px solid #e6e6e6'}}
          className='border-bottom'
          >
            <Icon
              className="trigger"
              type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
              onClick={this.toggle}
            />
          </Header>
          <Content style={{padding: 24, background: '#fff', minHeight: 280,
          }}
          >
          <App/>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

ReactDOM.render(<SiderDemo />, document.getElementById('container'));
