import React, { Component } from 'react'
import {Form,Input,Select} from 'antd';

import  './Add.less';

const FormItem = Form.Item;
const Option = Select.Option;

export default class Temp extends Component {
 

  render() {
    const { getFieldProps, getFieldError, isFieldValidating,getFieldDecorator} = this.props.props.form; 

    const formItemLayout = {//样式
      labelCol: { span: 7 },
      wrapperCol: { span: 12 },
    }; 
    const selectProps={
      
    }
    return (
      <Form >
      <FormItem
        {...formItemLayout}
        label="菜品名称："
        hasFeedback
        help={isFieldValidating('loading') ? '校验中...' : (getFieldError('loading') || []).join(', ')}
      >
         
        {/* 给input设置名字和规则方法    之  其二 */}
       {getFieldDecorator('loading', {
          rules: [{ required: true, message: 'Please input your note!' }],
        })(
          <Input />
        )}
      </FormItem>

      <FormItem
          label="菜品类型："
          {...formItemLayout}
          required
        >
          <Select style={{ width: 200 }}
            {...selectProps}
          >
            <Option value="特色汤面">特色汤面</Option>
            <Option value="酒水饮料">酒水饮料</Option>
            <Option value="烧烤系列">烧烤系列</Option>
            <Option value="美味盖饭">美味盖饭</Option>
            <Option value="小吃，肉夹馍">小吃，肉夹馍</Option>
            <Option value="海鲜" disabled>海鲜</Option>
          </Select>
        </FormItem>

      </Form >
    )
  }
}
