import React, { Component } from 'react'
import { Form, Input, Select, Upload, Icon, Modal, Switch, InputNumber } from 'antd';

import './Add.less';

const FormItem = Form.Item;
const Option = Select.Option;

export default class Temp extends Component {
  state = {
    previewVisible: false,//控制Modal是否显示（modal控制图片显示）
    fileList: [], //设置默认上传内容
  };
  handleCancel = () => this.setState({ previewVisible: false })
  componentDidMount() {
    console.log()
    const { spuId, spuName, bigImageUrl, littleImageUrl } = this.props.state.storeData
    this.setState({
      fileList: [{
        uid: "-" + spuId,
        name: spuName + '.png',
        status: 'done',
        url: littleImageUrl,
      }],
    }, () => {
      console.log(this.state.fileList)
    })
  }

  handlePreview = (file) => {
    this.setState({
      previewImage: file.url || file.thumbUrl,
      previewVisible: true,
    });
  }

  handleChange = ({ fileList }) => this.setState({ fileList })


  render() {
    const { getFieldProps, getFieldError, isFieldValidating, getFieldDecorator } = this.props.props.form;
    const data = this.props.state.data;
    const storeData = this.props.state.storeData;

    const formItemLayout = {//样式
      labelCol: { span: 7 },
      wrapperCol: { span: 12 },
    };
    const buttonsLayout = {//样式
      labelCol: {span: 4,offset	:2 },
      // wrapperCol: { span: 12 },
    };

    const countProps = getFieldProps('count', {//textarea 为ID
      initialValue: storeData.count,
      rules: [{ pattern: new RegExp(/^\d(\.\d+)?$/, "g"), message: '请输入合理折扣数字' }]
    });

    const selectProps = getFieldProps('promot', {
      initialValue: data.promot,
    });
    const { previewVisible, previewImage, fileList } = this.state;
    const uploadButton = (//添加 控制 按钮
      <div>
        <Icon type="plus" />
        <div className="ant-upload-text">Upload</div>
      </div>
    );

    return (
      <Form style={{ marginBottom: 40 }}>
        <FormItem
          {...formItemLayout}
          label="菜品折扣："
        >
          <InputNumber
            style={{ width: 320, fontSize: 13 }}
            placeholder="请输入菜品折扣，例如：1折扣=1"
            {...countProps}
          />
        </FormItem>

        <FormItem
          {...formItemLayout}
          label="菜品销售量："
        >
          {getFieldDecorator('saleVolume', {
            initialValue: storeData.saleVolume,
          })(
            <Input />
          )}
        </FormItem>

        <FormItem
          label="菜品库存："
          {...formItemLayout}
        >
          {/* require 代表前面的 * 符号 */}
          {getFieldDecorator('realStock', {
            initialValue: storeData.realStock,
          })(
            <Input />
          )}
        </FormItem>
        <FormItem
          label="当日库存："
          {...formItemLayout}
        >
          {/* require 代表前面的 * 符号 */}
          {getFieldDecorator('activityStock', {
            initialValue: storeData.activityStock,
          })(
            <Input />
          )}
        </FormItem>

        <FormItem
          label="菜品展示："
          style={{ marginBottom: -10 }}
          {...formItemLayout}
        >
          {getFieldDecorator('upload', {
            valuePropName: 'checked',
            initialValue: data.upload,
          })(
            <div className="clearfix">
              <Upload
                // action="//jsonplaceholder.typicode.com/posts/"
                listType="picture-card"
                fileList={fileList} //
                onPreview={this.handlePreview} //点击文件链接或预览图标时的回调
                onChange={this.handleChange}
              >
                {fileList.length >= 1 ? null : uploadButton}
              </Upload>
              <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
                <img alt="example" style={{ width: '100%' }} src={previewImage} />
              </Modal>
            </div>
          )}
        </FormItem>

        
        <Form.Item
          {...buttonsLayout}
          label="推荐:"
          style={{ fontSize: 15, color: "rgba(0, 0, 0, 0.9)" }}
        >
          {getFieldDecorator('switch1', {valuePropName: 'checked', checked: 1 })(
            <Switch style={{ margin: "0px 10px" }} />
          )}
        </Form.Item>
        <Form.Item
          {...buttonsLayout}
          label="折扣:"
          style={{ fontSize: 15, color: "rgba(0, 0, 0, 0.9)" }}
        >
          {getFieldDecorator('switch1', { valuePropName: 'checked', initialValue: 0 })(
            <Switch style={{ margin: "0px 10px" }} />
          )}
        </Form.Item>
      </Form >
    )
  }

}
