import React, {Fragment } from 'react';
import ReactDOM from 'react-dom';
// import 'antd/dist/antd.css';
import { Layout, Menu, Icon, Breadcrumb} from 'antd';
import { BrowserRouter as Router, Route, Link, Switch} from 'react-router-dom'
import createBrowserHistory from 'history/createBrowserHistory'

import App from './App';
import IconFont from './iconfont/font';
import Sidebar from './components/Sidebar/Sidebar';
import Header from './components/Header/Header';
import Home from './components/Content/Home/Home';
import Person from  './components/Content/Person/Person';
 import './index.css';

const history = createBrowserHistory()
let { Content} = Layout;
if(module.hot)
{
    module.hot.accept();
}

class MallProject extends React.Component {
  state = {
    collapsed: false,
  };
  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  }
  componentWillMount(){
    console.log(history)
  }

  render() {
    console.log(1111)

    return (
      <Router>  
      <Layout style={{height: "100%"}}>
       <Sidebar 
          trigger={null}
          collapsible
          collapsed={this.state.collapsed}
          onCollapse={this.onCollapse}
       />

     <Fragment >
      <Layout  style={{ background: 'rgb(240, 242, 245)'}}>
          <Header 
          toggle={this.toggle}
          collapsed={this.state.collapsed}
          > </Header>
          {/* < Home style={{backgroundColor:"red"}}/> */}


        <Route 
            path="/" 
            exact
            component={(props)=>{
                 return (
                  <Content 
                  style={{padding: 20, margin: "20px 0 0 16px",position:"relative"
                }}
                  >
                  <Home style={{position:"absolute"}}/>
                  </Content>
                 )//传数据     
            }} />   
       <Route 
            path="/Person" 
            component={(props)=>{
                 return <div>/Person/Person/Person</div> //传数据     
            }} />   
       <Route 
            path="/About" 
            component={(props)=>{
              return <div>"/About"/About"/About"/About</div>
            }} />  
        </Layout>
        </Fragment >

      </Layout>

       </Router> 

    );
  }
}

ReactDOM.render(<MallProject />, document.getElementById('container'));
