import React, { Component } from 'react'
import { Button, Pagination } from 'antd';
import IconFont from '../../../../iconfont/font'
import './Clerk.less'

export default class Temp extends Component {
  constructor() { //构造函数
    super();
    this.state = {
      data: '',
      listItems:'',
      page:1
    }
  }
  getData() { //请求数据函数
    fetch(`http://localhost:2000/pers/clerks`, {
      method: 'GET'
    })
    .then(res => res.json()).then(
      data => {
        this.setState({ ...data })
      })
  }
  ClerksInfo() {
    if (this.state.data) {
      // this.setState({page:this.state.data[0].result[0].ClerkData.length})
      // this.setState({page:20});
      // console.log(this.state.page)
       this.state.listItems=this.state.data[0].result[0].ClerkData.map((ele) => 
        <tr key={ele.num}>
        <td>{ele.num}</td>
        <td>{ele.name}</td>
        <td>{ele.sex}</td>
        <td>{ele.cardNum}</td>
        <td>{ele.age}</td>
        <td>{ele.birthday}</td>
        <td>{ele.state}</td>
        <td>{ele.jobTitle}</td>
        <td>{ele.hiredate}</td>
        <td>
      <Button type="primary" size="small" style={{ marginRight: 10, fontSize: 13, width: 60, height: 25, borderRadius: 5 }}>编辑</Button>
      <Button type="primary" size="small" style={{ fontSize: 13, width: 60, height: 25, borderRadius: 5 }}>删除</Button>
    </td>
        </tr>)
     return  (<tbody>{this.state.listItems}</tbody> ) 
    }
  }
  componentWillMount() {
    this.getData();
  }
  ChangePage(page){//<Pagination/>组件自带回调函数
      console.log(page)
      this.setState({page:page},()=>{

      })
      console.log(this.state.page)//setstate
  }
  render() {
    //分页组件提取出来，先进行一步数据是否获得到的判断，因为render渲染多次，前几次没有数据
    let Page = null;
    let {data} = this.state;
    if(data){
      // data[0].result[0].ClerkData
      Page = <Pagination 
      defaultCurrent={1}  
      pageSize={5}	
      total={data[0].result[0].ClerkData.length}
      onChange={this.ChangePage.bind(this)}	  
      style={{
        marginRight: 30, marginTop: 25, float: "right"
      }} />
    }
    return (
      <section className="clerk-info">
        {/*   员工信息组件  */}
        <div className="app-container">
          <div className="el-serch-wrap">
            <div className="el-title-body">
              <div>
                <IconFont type="mall-doc-glass" style={{ fontSize: 16, marginRight: 5 }} />
                <span>筛选检索</span>
                <button className="add">查询结果</button>
              </div>
              <div className="el-form-item">
                <span>输入检索:</span>
                <input type="text" placeholder="请输入姓名">
                </input>
              </div>
            </div>
          </div>
          <div className="el-title">
            <div className="el-title-body">
              <IconFont type="mall-doc-list" style={{ fontSize: 16, marginRight: 5 }} />
              <span>数据列表</span>
              <button className="add">添加人员信息</button>
            </div>
          </div>
          <main className="table-container">
            <table cellPadding="0" cellSpacing="0">
              <thead>
                <tr>
                  <th>工号</th>
                  <th>姓名</th>
                  <th>性别</th>
                  <th>身份证号</th>
                  <th>出生年月</th>
                  <th>年龄</th>
                  <th>入职时间</th>
                  <th>职位</th>
                  <th>状态</th>
                  <th>操作</th>
                </tr>
              </thead>
              {this.ClerksInfo()}
            </table>
          </main>
         {Page}
        </div>
      </section>
    )
  }
}
