import React, { Component } from 'react'
import { Button, Pagination, Form, Modal, Input, message } from 'antd';
import IconFont from '../../../../iconfont/font';
import { Link, withRouter } from 'react-router-dom';
import './Clerk.less';

const FormItem = Form.Item;

class Temp extends Component {
  constructor() { //构造函数
    super();
    this.listItems = ""; //  人员信息列表数据;
    this.filterData = "";  // 过滤后的数据;
    this.itemsName=" " ;
     // From 表单渲染时候的英文;
    this.state = {
      data: '',
      storeData: "",
      current: 1,
      refresh: true,
      visible: false,
    }
  }
  getData() { //请求数据函数
    fetch(`http://localhost:2000/pers/clerks`, {
      method: 'GET'
    })
      .then(res => res.json()).then(
        data => {
          this.setState({
            data: data.data,
            storeData: data.data
          })
        })
  }
  componentDidMount() {
    //挂载前,请求数据函数
    this.getData();
  }
  async EditClick(index, link) {
    // e.preventDefault();
    console.log(index);
    // pathname:'/pers/clerksDetails?num='+index,
    // state:this.state.data[0].ClerkData.filter(e=>e.num==index)
    // let vcode =[{aa:1}]
    await this.props.history.push({
      pathname: "/pers/clerksDetails",
      state: this.state.data[0].ClerkData.filter(e => e.num == index),
      search: '?num=' + index
    });
    // this.props.history.push('/pers/clerksDetails?num='+index);
    // this.props.history.push('/pers/clerksDetails?num='+index);
    // this.props.location.state=1111
    // console.log(this.props)
  }
  renderFunc = (ele) => { //人员信息列表部分渲染;
    return (
      <tr key={ele.num} id={ele.num}>
        <td>{ele.num}</td>
        <td>{ele.name}</td>
        <td>{ele.sex}</td>
        <td>{ele.cardNum}</td>
        <td>{ele.age}</td>
        <td>{ele.birthday}</td>
        <td>{ele.hiredate}</td>
        <td>{ele.jobTitle}</td>
