  }
  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }
  error = () => {
    message.error("It doesn't exist !");
  };
  handleResetClick() {
    document.getElementById("indexName").value = ""
    this.setState({ current: 1 })
    this.getData()//如果结果不存在重新请求数据,也可以提前把数据存在
  }

  render() {
    let Page = null;
    let { data } = this.state;
    const { getFieldProps, getFieldError, isFieldValidating, getFieldDecorator } = this.props.form;
    //--------------------    表单部分    ---------------
    const title = ["姓名", "性别", "身份证号", "出生年月", "年龄", "入职时间", "职位", "状态"];
    const formItemLayout = {//样式
      // span是整体左移 pull是label左移   
      labelCol: {
        xs: { span: 24 },
        offset: 2,
        sm: { span: 6 }
      },//左侧 offset:3
      wrapperCol: { span: 10 },//input 长度控制
    };
    if (data) {
      //  底部页码组件
      Page = <Pagination
        defaultCurrent={1}
        pageSize={5}
        total={data[0].ClerkData.length}
        onChange={this.ChangePage.bind(this)}
        style={{
          marginRight: 30, marginTop: 25, float: "right"
        }} />

      //  目的给Form.Item 规范命名让其和数据key值相同
