import React from 'react';
import ReactDOM from 'react-dom';
// import 'antd/dist/antd.css';
import { Layout, Menu, Icon, Breadcrumb } from 'antd';
import App from './App';
import IconFont from './iconfont/font';
import Sidebar from './components/Sidebar/Sidebar'

console.log(Sidebar)

if(module.hot)
{
    module.hot.accept();
}

const { Header, Sider, Content } = Layout;
const SubMenu = Menu.SubMenu;

// const IconFont = Icon.createFromIconfontCN({
//   scriptUrl: '//at.alicdn.com/t/font_1050856_g3xhywuvbnb.js',   //阿里巴巴图标引用地址
// });



class SiderDemo extends React.Component {
  state = {
    collapsed: false,
  };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  }

  render() {
    return (
      <Layout style={{height:" 100%",border:" 1px solid rgb(230, 230, 230)",background:red}}>
       <Sidebar
         trigger={null}
         collapsible
         collapsed={this.state.collapsed}
         onCollapse={this.onCollapse}
         style={{background:"white"}}  
        />
        <Layout  style={{ background: 'rgb(240, 242, 245)'}}>
          <Header style={{ background: '#fff', padding: 0,height: "50px",border:'1px solid #e6e6e6',marginLeft:"16px"}}
          className='border-bottom'
          >
            <Icon
              className="trigger"
              type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
              onClick={this.toggle}
              style={{lineHeight:"50px"}}
            />
            面包屑位置
          </Header>
          <Content style={{padding: 20, background: '#fff', minHeight: 280,margin: "24px 0 0 16px"
        }}
          >
          <App/>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

ReactDOM.render(<SiderDemo />, document.getElementById('container'));
