import React, { Component } from 'react'
import {
  Form, Select, InputNumber, Button, Input
} from 'antd';
import { Link, withRouter } from 'react-router-dom';
import './Edit.less';

const { Option } = Select;
const FormItem = Form.Item;
const ButtonGroup = Button.Group;
const title = ["姓名", "身份证号", "出生年月", "年龄", "入职时间", "职位"];

class Temp extends Component {
  handleReset(e) {
    //resetFields重置表单内容
    e.preventDefault();
    this.props.props.form.resetFields();
  };
  userExists(rule, value, callback) {
    //当做validator函数来用,用来作为判断条件
    if (!value) {
      callback();
    } else {
      setTimeout(() => {
        if (value === '宫爆鸡丁') {
          callback([new Error('抱歉，该菜名已被占用。')]);
        } else {
          callback();
        }
      }, 800);
    }
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  }
  normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  }
  render() {
    let arr=Object.keys( this.props.location.state[0])
    let TempArr=arr.filter((currentValue,index)=> currentValue!="num"&&currentValue!="sex"&&currentValue!="state")

    console.log(this.props.location.state[0])//数据

    // console.log(this.props.location.state[0].num)
    // let temp = delete this.props.location.state[0].num
    // temp=delete this.props.location.state[0].sex
    //分页组件提取出来，先进行一步数据是否获得到的判断，因为render渲染多次，前几次没有数据
    // return (<div>21321321</div>)
    const { getFieldProps, getFieldError, isFieldValidating, getFieldDecorator } = this.props.form;

    const selectProps = getFieldProps('select', {
      initialValue: 2,
      rules: [
        { required: true, message: '请选择菜品类型' },
      ],
    });

    const formItemLayout = {//样式
      // span是整体左移 pull是label左移   
      labelCol: { span: 6, pull: 1 },//左侧 offset:3
      wrapperCol: { span: 10 },//input 长度控制
    };
    return (
      <section className="clerk-info">
        <div className="wrap">
          <Form >
            <FormItem
              label="状态:"
              {...formItemLayout}
              required
            >
             {getFieldDecorator("state", {initialValue:this.props.location.state[0].state
                })(
                  <Select
                >
                  <Option value="在职">在职</Option>
                  <Option value="离职">离职</Option>
                </Select>
                )}
            </FormItem>

            <FormItem
              label="性别:"
              {...formItemLayout}
              required
            >
             {getFieldDecorator("sex", {initialValue:this.props.location.state[0].sex
                })(
                  <Select
                >
                  <Option value="男">男</Option>
                  <Option value="女">女</Option>
                  <Option value="Other" disabled>你还想选啥,变态</Option>
                </Select>
                )}
            </FormItem>
            {title.map((ele, index) =>
              <FormItem
                {...formItemLayout}
                key={index}
                label={ele}
                hasFeedback
                // help={isFieldValidating('name') ? '校验中...' : (getFieldError('name') || []).join(', ')}
              >
                {getFieldDecorator(TempArr[index], 
                {initialValue:Object.values(this.props.location.state[0])[index],
                  rules: [
                    { required: true, min: 1, message: '菜品名至少为 1 个字符' },
                    { validator: this.userExists.bind(this) }
                  ]
                })(
                  <Input/>
                )}
              </FormItem>
              //              <FormItem>
              //  {getFieldDecorator(Object.keys(this.props.location.state[0])[index]),{initialValue: data.radioButton,
              // rules: [
              //   { required: true, min: 1, message: '菜品名至少为 1 个字符' },
              //   { validator: this.userExists.bind(this) }
              // ]
              // })(
              //    <Input {...nameProps} placeholder=" 输入 宫爆鸡丁 看看"/>
              // )}
              //  </FormItem>


              // <FormItem
              //   {...formItemLayout}
              //   label={title[0]}
              //   hasFeedback
              //   help={isFieldValidating('name') ? '校验中...' : (getFieldError('name') || []).join(', ')}
              // >
              // </FormItem>
            )}
          </Form >
        </div>
      </section>
    )
  }
}
const WrappedDemo = Form.create({ name: 'validate_other' })(Temp);

export default withRouter(WrappedDemo)
// export default Temp
