export const fetchPosts = subreddit => dispatch => {
    // dispatch(getMemberInfo(subreddit))
    return fetch(`http://localhost:2000/pers/member`, {
            method: 'GET',
        })
        .then(res => res.json())
        .then(data => dispatch(setMemberInfo(data)))//dispatch(receivePosts(subreddit, json))
}




