import React, { PureComponent } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import './Echart.less'

const data = [
  {
    date: '2018-02-21', '销售额': 4000, '订单': 70, 
  },
  {
    date: '2018-02-22', '销售额': 3000, '订单': 58, 
  },
  {
    date: '2018-02-23', '销售额': 2000, '订单': 80,
  },
  {
    date: '2018-02-24', '销售额': 2780, '订单': 98, 
  },
  {
    date: '2018-02-25', '销售额': 1890, '订单': 248, 
  },
  {
    date: '2018-02-26', '销售额': 2390, '订单': 209, 
  },
  {
    date: '2018-02-27', '销售额': 3490, '订单': 180, 
  },
];

export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/zjb47e83/';

  render() {
    return (
      <LineChart
        width={600}
        height={400}
        data={data}
        margin={{
          top: 50, right: 20, left: 35,bottom:40
        }}
        >
        <CartesianGrid strokeDasharray="5 5" />
        <XAxis dataKey="date" />
        <YAxis yAxisId="left" />
        <YAxis yAxisId="right" orientation="right" />
        <Tooltip />

        <Legend />
        <Line yAxisId="left" type="monotone" dataKey="销售额" stroke="#8884d8" activeDot={{ r: 6 }} />
        <Line yAxisId="right" type="monotone" dataKey="订单" stroke="#82ca9d" activeDot={{ r: 6 }} />
      </LineChart>
    );
  }
}
