import React, { Component } from 'react'
import {
    Form, Select, InputNumber,Button
  } from 'antd';
import { Link,withRouter} from 'react-router-dom';
import './Edit.less';
  
const { Option } = Select;
const FormItem = Form.Item;

class Temp extends Component {
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
          if (!err) {
            console.log('Received values of form: ', values);
          }
        });
  }
      normFile = (e) => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
          return e;
        }
        return e && e.fileList;
  }


  render() {
    console.log(this.props.location.state)//数据
    //分页组件提取出来，先进行一步数据是否获得到的判断，因为render渲染多次，前几次没有数据
    //  姓名	性别	身份证号	出生年月	年龄	入职时间	职位	状态
    // return (<div>21321321</div>)
    const { getFieldDecorator } = this.props.form;
    const formItemLayout = {
      labelcol: { span: 7 },
      wrappercol: { span: 12 },
    };

    return(

      
      <section className="clerk-info">
     <div className="wrap">
     <Form >
        <FormItem
          {...formItemLayout}
          label="菜品名称："
          hasFeedback
          help={isFieldValidating('name') ? '校验中...' : (getFieldError('name') || []).join(', ')}
        >
          <Input {...nameProps} placeholder=" 输入 宫爆鸡丁 看看"
          />
        </FormItem>

        <FormItem
          label="菜品类型："
          {...formItemLayout}
          required
        >
          <Select style={{ width: 200 }}
            {...selectProps}
          >
            <Option value="特色汤面">特色汤面</Option>
            <Option value="酒水饮料">酒水饮料</Option>
            <Option value="烧烤系列">烧烤系列</Option>
            <Option value="美味盖饭">美味盖饭</Option>
            <Option value="小吃，肉夹馍">小吃，肉夹馍</Option>
            <Option value="海鲜" disabled>海鲜</Option>
          </Select>
        </FormItem>

        <FormItem
          {...formItemLayout}
          label="菜品价格："
        >
          {/*
          给input设置名字和规则方法  之 其二
         {getFieldDecorator('note', {
            rules: [{ required: true, message: 'Please input your note!' }],
          })(
            <Input />
          )} */}
          <InputNumber
            style={{ width: 320 }}
            type="textarea"
            // placeholder="菜品名称"
            id="price"
            name="price"
            {...textareaProps}
          />
        </FormItem>

        <FormItem
          id="control-textarea"
          label="菜品介绍："
          {...formItemLayout}
        >
          <textarea id="control-textarea" style={{
            borderRadius: 6, textAlign: "left", verticalAlign: "top", border: "1px solid #d9d9d9", fontSize: 14, lineHeight: "20px", textIndent: "1em"
          }}
            {...priceProps}
            placeholder="Please enter..."
            cols="45"
            rows="4" />
        </FormItem>
        <ButtonGroup style={{ borderRadius: 15, marginTop: 30 }}>
          <Button
            onClick={this.handleReset.bind(this)}
            style={{ marginLeft: 400 }}>Reset</Button>
        </ButtonGroup>
      </Form >
     </div>
      </section>
    )
  }
}
const WrappedDemo = Form.create({ name: 'validate_other' })(Temp);

export default withRouter(WrappedDemo)
// export default Temp
