import React, { PureComponent } from 'react';
import {
  AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip,Legend,
} from 'recharts';

const data = [
  {
    date: '2018-02-21', '销售额': 2000, '订单': 70, 
  },
  {
    date: '2018-02-22', '销售额': 1700, '订单': 58, 
  },
  {
    date: '2018-02-23', '销售额': 1680, '订单': 80,
  },
  {
    date: '2018-02-24', '销售额': 2780, '订单': 98, 
  },
  {
    date: '2018-02-25', '销售额': 1790, '订单': 78, 
  },
  {
    date: '2018-02-26', '销售额': 2490, '订单': 99, 
  },
  {
    date: '2018-02-27', '销售额': 1870, '订单': 80, 
  },
];

export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/c1rLyqj1/';

  render() {
    return (
      <AreaChart
        width={500}
        height={400}
        data={data}
        margin={{
          top: 10, right: 30, left: 0, bottom: 0,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        {/* <XAxis dataKey="name" /> */}
        <Tooltip />
        <YAxis yAxisId="left" />
        {/* <YAxis yAxisId="right" orientation="right" /> */}
        <Area type="monotone" dataKey="销售额" stackId="1" stroke="#8884d8" fill="#8884d8" />
        <Area type="monotone" dataKey="订单" stackId="1" stroke="#82ca9d" fill="#82ca9d" />
      </AreaChart>
    );
  }
}





